def test_codigo_com_erro(arquivo, arvore, msgErroEsperada):
	semantico = None
	ok = False
	try:
		semantico = AnalisadorSemantico(arvore)
	except:
		raise AssertionError("Erro ao instanciar a classe AnalisadorSemantico")
	if semantico:
		try:
			semantico.analisar()
		except SemanticException as e:
			ok = True
			assert re.match(msgErroEsperada[0], str(e)) != None, f"A mensagem de erro está diferente: {str(e)}"
	if not ok:
		raise AssertionError("A classe AnalisadorSemantico não lançou a exceção esperada")



